}

const styles = StyleSheet.create({
  button: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    // paddingVertical: 24,
    // paddingHorizontal: 24,
    // alignSelf: 'stretch',
    // flexGrow: 0,
    borderWidth: 4,
    borderRadius: 16,
    height: 88,
    width: 500,
  },
  textFrame: {
    display: 'flex',
    order: 0,
    flexDirection: 'row',
  },
  textStyle: {
    display: 'flex',
    fontFamily: 'OpenSans-SemiBold',
    fontStyle: 'normal',
    fontWeight: '600',
    fontSize: 32,
